TERRAIN_FILTER.Print = function (filter)
	local val = ""
	for i,v in ipairs(filter) do
		val = val .." ".. GROUND_NAMES[v]
	end
	return val
end

local rooms = require("map/rooms")

terrain={rooms=rooms, filter=TERRAIN_FILTER}
